from collections import Counter
import re
from langflow.custom.custom_component.component import Component
from langflow.io import MessageTextInput, Output, DataInput
from langflow.schema import Message, Data

class MajorityVotingComponent(Component):
    display_name = "Majority Voting"
    description = "Takes a list of responses and returns the majority answer."
    icon = "fa-solid fa-vote-yea"
    name = "MajorityVotingComponent"

    inputs = [
        DataInput(
            name="responses",
            display_name="List of Responses",
            info="A list of answer strings from multiple runs",
            is_list=True,
            input_types=["Data"],
        ),
        MessageTextInput(
            name="tie_break_policy",
            display_name="Tie Break Policy",
            info="What to do if there's a tie: first / random / all",
            value="first",
        ),
    ]

    outputs = [
        Output(
            name="majority_answer",
            display_name="Majority Answer",
            method="compute_majority",
        )
    ]

    def _to_string(self, item) -> str:
        """Convert Data/Message/str to plain string."""
        if isinstance(item, str):
            return item.strip()
        if isinstance(item, Data):
            if hasattr(item, "data") and isinstance(item.data, dict):
                return str(item.data.get("text", item.data)).strip()
            return str(getattr(item, "data", item)).strip()
        if isinstance(item, Message):
            return str(getattr(item, "text", item)).strip()
        return str(item).strip() if item is not None else ""

    def _extract_answer(self, response: str) -> str:
        """Extract the numerical answer from response (last number found)."""
        # Extract all numbers (including decimals and negatives)
        numbers = re.findall(r'-?\d+\.?\d*', response)
        if numbers:
            # Return the last number found (usually the final answer)
            return numbers[-1]
        # If no numbers found, return original response
        return response.strip()

    def compute_majority(self) -> Message:
        """Compute majority answer from multiple responses."""
        resp_items = self.responses

        # Extract responses from Data wrapper if needed
        if isinstance(resp_items, list) and len(resp_items) == 1 and isinstance(resp_items[0], Data):
            resp_items = resp_items[0]

        if isinstance(resp_items, Data) and isinstance(resp_items.data, dict):
            resp_items = resp_items.data.get("responses", [str(resp_items.data)])
        elif isinstance(resp_items, Message):
            resp_items = [resp_items.text if hasattr(resp_items, 'text') else str(resp_items)]
        elif not isinstance(resp_items, list):
            resp_items = [resp_items]

        # Convert all items to strings
        plain_answers = [self._to_string(item) for item in resp_items]

        if not plain_answers:
            return Message(text="Error: no responses provided for voting.")

        # Extract answers based on mode
        extracted_answers = [self._extract_answer(ans) for ans in plain_answers]

        # Count occurrences (case-insensitive)
        counter = Counter(a.lower() for a in extracted_answers)
        most_common, count = counter.most_common(1)[0]

        # Handle ties
        tied = [k for k, v in counter.items() if v == count]
        if len(tied) > 1:
            tie_handlers = {
                "all": lambda: ", ".join(tied),
                "random": lambda: __import__('random').choice(tied),
            }
            winner = tie_handlers.get(self.tie_break_policy.lower(), lambda: extracted_answers[0])()
        else:
            # Find original-case version of winner
            winner = next((ans for ans in extracted_answers if ans.lower() == most_common), extracted_answers[0])

        # Simple, clean response
        confidence_pct = (100 * count) // len(plain_answers)
        return Message(text=f"{winner} (confidence: {count}/{len(plain_answers)} = {confidence_pct}%)")

from langflow.custom.custom_component.component import Component
from langflow.inputs.inputs import HandleInput
from langflow.schema.data import Data
from langflow.schema.dataframe import DataFrame
from langflow.template.field.base import Output


class LoopComponent(Component):
    display_name = "Loop"
    description = (
        "Iterates over a list of Data objects, outputting one item at a time and aggregating results from loop inputs."
    )
    documentation: str = "https://docs.langflow.org/components-logic#loop"
    icon = "infinity"

    inputs = [
        HandleInput(
            name="data",
            display_name="Inputs",
            info="The initial list of Data objects or DataFrame to iterate over.",
            input_types=["DataFrame"],
        ),
    ]

    outputs = [
        Output(display_name="Item", name="item", method="item_output", allows_loop=True, group_outputs=True),
        Output(display_name="Done", name="done", method="done_output", group_outputs=True),
    ]

    def initialize_data(self) -> None:
        """Initialize the data list, context index, and aggregated list."""
        if self.ctx.get(f"{self._id}_initialized", False):
            return

        # Ensure data is a list of Data objects
        data_list = self._validate_data(self.data)

        # Store the initial data and context variables
        self.update_ctx(
            {
                f"{self._id}_data": data_list,
                f"{self._id}_index": 0,
                f"{self._id}_aggregated": [],
                f"{self._id}_initialized": True,
            }
        )

    def _validate_data(self, data):
        """Validate and return a list of Data objects."""
        if isinstance(data, DataFrame):
            return data.to_data_list()
        if isinstance(data, Data):
            return [data]
        if isinstance(data, list) and all(isinstance(item, Data) for item in data):
            return data
        msg = "The 'data' input must be a DataFrame, a list of Data objects, or a single Data object."
        raise TypeError(msg)

    def evaluate_stop_loop(self) -> bool:
        """Evaluate whether to stop item or done output."""
        current_index = self.ctx.get(f"{self._id}_index", 0)
        data_length = len(self.ctx.get(f"{self._id}_data", []))
        return current_index > data_length

    def item_output(self) -> Data:
        """Output the next item in the list or stop if done."""
        self.initialize_data()
        current_item = Data(text="")

        if self.evaluate_stop_loop():
            self.stop("item")
        else:
            # Get data list and current index
            data_list, current_index = self.loop_variables()
            if current_index < len(data_list):
                # Output current item and increment index
                try:
                    current_item = data_list[current_index]
                except IndexError:
                    current_item = Data(text="")
            self.aggregated_output()
            self.update_ctx({f"{self._id}_index": current_index + 1})

        # Now we need to update the dependencies for the next run
        self.update_dependency()
        return current_item

    def update_dependency(self):
        item_dependency_id = self.get_incoming_edge_by_target_param("item")
        if item_dependency_id not in self.graph.run_manager.run_predecessors[self._id]:
            self.graph.run_manager.run_predecessors[self._id].append(item_dependency_id)

    def done_output(self) -> DataFrame:
        """Trigger the done output when iteration is complete."""
        self.initialize_data()

        if self.evaluate_stop_loop():
            self.stop("item")
            self.start("done")

            aggregated = self.ctx.get(f"{self._id}_aggregated", [])

            return DataFrame(aggregated)
        self.stop("done")
        return DataFrame([])

    def loop_variables(self):
        """Retrieve loop variables from context."""
        return (
            self.ctx.get(f"{self._id}_data", []),
            self.ctx.get(f"{self._id}_index", 0),
        )

    def aggregated_output(self) -> list[Data]:
        """Return the aggregated list once all items are processed."""
        self.initialize_data()

        # Get data list and aggregated list
        data_list = self.ctx.get(f"{self._id}_data", [])
        aggregated = self.ctx.get(f"{self._id}_aggregated", [])
        loop_input = self.item
        if loop_input is not None and not isinstance(loop_input, str) and len(aggregated) <= len(data_list):
            aggregated.append(loop_input)
            self.update_ctx({f"{self._id}_aggregated": aggregated})
        return aggregated

